const itemsPerPage = 8;

function isSmallScreen() {
    return window.matchMedia('(max-width: 1024px)').matches;
}

// Initialize the first tab
document.addEventListener('DOMContentLoaded', () => {
    document.querySelector('.tablinks').classList.add('active');
    document.querySelector('.topicTabContent').classList.add('active');
    updateTabContent(1, 1);
});

// Event delegation for pagination
document.addEventListener('click', function (e) {
    const target = e.target;
    const currentTab = document.querySelector('.tablinks.active').dataset.tab;
    const totalPages = Math.ceil(topicData[currentTab].length / itemsPerPage);
    const currentPage = parseInt(document.querySelector(`#tab-${currentTab} .page-number.active`)?.dataset.page || '1', 10);

    if (target.classList.contains('first-page')) {
        updateTabContent(currentTab, 1);
    } else if (target.classList.contains('prev-page') && currentPage > 1) {
        updateTabContent(currentTab, currentPage - 1);
    } else if (target.classList.contains('next-page') && currentPage < totalPages) {
        updateTabContent(currentTab, currentPage + 1);
    } else if (target.classList.contains('last-page')) {
        updateTabContent(currentTab, totalPages);
    } else if (target.classList.contains('page-number')) {
        const page = parseInt(target.dataset.page, 10);
        updateTabContent(currentTab, page);
    }
});

// Tab switching logic
document.querySelectorAll('.tablinks').forEach((tab) => {
    tab.addEventListener('click', function () {
        const tabId = this.dataset.tab;

        // Deactivate all tabs
        document.querySelectorAll('.tablinks').forEach((t) => t.classList.remove('active'));
        document.querySelectorAll('.topicTabContent').forEach((content) => content.classList.remove('active'));

        // Activate the selected tab
        this.classList.add('active');
        document.querySelector(`#tab-${tabId}`).classList.add('active');

        // Load the first page of the selected tab
        updateTabContent(tabId, 1);
    });
});

// Function to update the tab content
function updateTabContent(tabId, currentPage) {
    const tabContent = document.querySelector(`#tab-${tabId}`);
    const topicWrap = tabContent.querySelector('.topicWrap');
    const totalPages = Math.ceil(topicData[tabId].length / itemsPerPage);

    // Render the current page's content
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    const currentItems = topicData[tabId].slice(startIndex, endIndex);

    topicWrap.innerHTML = currentItems
        .map(
            (item) => `
    <div class="topicItem">
        <div class="imgWrap">
            <img src="${item.image}" alt="Topic Image" />
            <p class="gameAmount">${item.gameAmount}款</p>
        </div>
        <div class="topicDetailWrap">
            <div class="titleWrap">
                <div class="postTitle">${item.title}</div>
                <div class="gameAmount mobile">共 <span>${item.gameAmount}</span> 款游戏</div>
                <img class="arrowLeft" src="./assets/images/rank/arrowLeft.png" alt="Arrow Right" />
            </div>
            <div class="updateWrap">
                <img src="./assets/images/topic/updateIcon.png" alt="Update Icon" />
                <p>更新于 ${item.date}</p>
            </div>
            <div class="app-list">
                ${item.apps
                    .map(
                        (app) => `
                        <div class="appWrap">
                            <img src="${app.image}" alt="App Image" />
                            <p class="appName">${app.name}</p>
                            <div class="viewNow">查看</div>
                        </div>
                        `
                    )
                    .join('')}
            </div>
        </div>
    </div>
    `
        )
        .join('');

    // Update pagination
    updatePagination(tabId, currentPage, totalPages);
}

// Function to update pagination
function updatePagination(tabId, currentPage, totalPages) {
    const pagination = document.querySelector(`#tab-${tabId} .pagination`);
    const isSmall = isSmallScreen();

    const pageNumbers = [];
    if (isSmall) {
        // Small screens: Always show 3 consecutive pages
        let startPage = Math.max(1, currentPage - 1);
        let endPage = Math.min(totalPages, currentPage + 1);

        if (startPage === 1) {
            endPage = Math.min(3, totalPages);
        } else if (endPage === totalPages) {
            startPage = Math.max(1, totalPages - 2);
        }

        for (let i = startPage; i <= endPage; i++) {
            pageNumbers.push(
                `<button class="page-number ${i === currentPage ? 'active' : ''}" data-page="${i}">${i}</button>`
            );
        }
    } else {
        if (totalPages <= 3) {
            for (let i = 1; i <= totalPages; i++) {
                pageNumbers.push(
                    `<button class="page-number ${i === currentPage ? 'active' : ''}" data-page="${i}">${i}</button>`
                );
            }
        } else {
            if (currentPage === 1 || currentPage === 2) {
                for (let i = 1; i <= 3; i++) {
                    pageNumbers.push(
                        `<button class="page-number ${i === currentPage ? 'active' : ''}" data-page="${i}">${i}</button>`
                    );
                }
                pageNumbers.push('<span class="dots">...</span>');
                pageNumbers.push(`<button class="page-number" data-page="${totalPages}">${totalPages}</button>`);
            } else if (currentPage === totalPages || currentPage === totalPages - 1) {
                pageNumbers.push(`<button class="page-number" data-page="1">1</button>`);
                pageNumbers.push('<span class="dots">...</span>');
                for (let i = totalPages - 2; i <= totalPages; i++) {
                    pageNumbers.push(
                        `<button class="page-number ${i === currentPage ? 'active' : ''}" data-page="${i}">${i}</button>`
                    );
                }
            } else {
                pageNumbers.push(`<button class="page-number" data-page="1">1</button>`);
                pageNumbers.push('<span class="dots">...</span>');
                for (let i = currentPage - 1; i <= currentPage + 1; i++) {
                    pageNumbers.push(
                        `<button class="page-number ${i === currentPage ? 'active' : ''}" data-page="${i}">${i}</button>`
                    );
                }
                pageNumbers.push('<span class="dots">...</span>');
                pageNumbers.push(`<button class="page-number" data-page="${totalPages}">${totalPages}</button>`);
            }
        }
    }

    pagination.innerHTML = `
        <button class="first-page" ${currentPage === 1 ? 'disabled' : ''}>首页</button>
        <button class="prev-page pagination-button" ${currentPage === 1 ? 'disabled' : ''}>${isSmall ? '<' : '上一页'}</button>
        ${pageNumbers.join('')}
        <button class="next-page pagination-button" ${currentPage === totalPages ? 'disabled' : ''}>${isSmall ? '>' : '下一页'}</button>
        <button class="last-page" ${currentPage === totalPages ? 'disabled' : ''}>尾页</button>
    `;
}

// Add resize event listener to update pagination on resize
window.addEventListener('resize', () => {
    const currentTab = document.querySelector('.tablinks.active').dataset.tab;
    const currentPage = parseInt(document.querySelector(`#tab-${currentTab} .page-number.active`)?.dataset.page || '1', 10);
    updatePagination(currentTab, currentPage, Math.ceil(topicData[currentTab].length / itemsPerPage));
});



